-- Add missing columns to existing tables (if they don't exist)
DO $$
BEGIN
    -- Add remote_work column to jobs table if it doesn't exist
    IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'jobs' AND column_name = 'remote_work') THEN
        ALTER TABLE public.jobs ADD COLUMN remote_work BOOLEAN DEFAULT false;
    END IF;
    
    -- Add benefits column to jobs table if it doesn't exist
    IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'jobs' AND column_name = 'benefits') THEN
        ALTER TABLE public.jobs ADD COLUMN benefits TEXT[];
    END IF;
END $$;

-- Insert sample companies (only if they don't exist)
ON CONFLICT (id) DO NOTHING;

-- Insert sample jobs (only if they don't exist)
ON CONFLICT (id) DO NOTHING;
